import React from 'react';

function Book(props) {

    function reverseName(name) {
        return name.split(", ").reverse().join(" ");        
    }

    const author = props.authors.length === 0 ? '' : ` by ${reverseName(props.authors[0].name)}`;

    return (
        <li><b>{props.title}</b> {author}</li>

    )
}

export default Book;
