import React from 'react';

function BookPage(props) {
    let LSpropsObject;
    let bookid = props.match.params.bookid;

    const getBookId = () => {
            console.log('getting object id');
            console.log(bookid);
    }
    getBookId();

    const restoreFromLocalStorage = () => {
        LSpropsObject = JSON.parse(localStorage.getItem(`book${bookid}`));
        console.log(LSpropsObject);
    }

    restoreFromLocalStorage();

    function reverseName(name) {
        return name.split(", ").reverse().join(" ");        
    }

    const author = LSpropsObject.authors.length === 0 ? '' : `Author: ${reverseName(LSpropsObject.authors[0].name)}, ${LSpropsObject.authors[0].birth_year} - ${LSpropsObject.authors[0].death_year}`;

    const readInBrowser = LSpropsObject.formats["text/html; charset=utf-8"] || LSpropsObject.formats["text/html; charset=iso-8859-1"] || LSpropsObject.formats["text/html; charset=us-ascii"];


    const readAppleBooks = LSpropsObject.formats["application/epub+zip"];

    const readKindle = LSpropsObject.formats["application/x-mobipocket-ebook"];

    return (
        <div className="bookpage">
            <p>{LSpropsObject.title}</p>
            <p>{author}</p>
            <p>Download count in the last 30 days: {LSpropsObject.download_count.toLocaleString()}</p>
            <p>Reading options:</p>
            <p><a href={readInBrowser} target="_blank" rel="noreferrer">Read in your browser</a></p>
            <p><a href={readAppleBooks} target="_blank" rel="noreferrer">Read via Apple Books</a> (will start download, may take a moment)</p>
            <p><a href={readKindle} target="_blank" rel="noreferrer">Read via Kindle</a> (will start download, can be transmitted to Kindle via USB or email)</p>
            <p>Subjects:</p>
            <ul>
                {LSpropsObject.subjects.map(subject => (
                    <li key={subject}>{subject}</li>
                ))}
            </ul>



        </div>
    )
}

export default BookPage;
