import React from 'react';

function BookPage(props) {
    let LSpropsObject;
    let bookid = props.match.params.bookid;

    const getBookId = () => {
            console.log('getting object id');
            console.log(bookid);
    }
    getBookId();

    const restoreFromLocalStorage = () => {
        LSpropsObject = JSON.parse(localStorage.getItem(`book${bookid}`));
        console.log(LSpropsObject);
    }

    restoreFromLocalStorage();

    function reverseName(name) {
        return name.split(", ").reverse().join(" ");        
    }

    const author = props.authors.length === 0 ? '' : `Author: ${reverseName(props.authors[0].name)}`;

    return (
        <div className="bookpage">
            <p>{LSpropsObject.title}</p>
            <p>{author}</p>

        </div>
    )
}

export default BookPage;
