import React from 'react';

function BookPage(props) {
    let LSpropsObject;
    let bookid = props.match.params.bookid;

    const getBookId = () => {
            console.log('getting object id');
            console.log(bookid);
    }
    getBookId();

    const restoreFromLocalStorage = () => {
        LSpropsObject = JSON.parse(localStorage.getItem(`book${bookid}`));
        console.log(LSpropsObject);
    }

    restoreFromLocalStorage();

    function reverseName(name) {
        return name.split(", ").reverse().join(" ");        
    }

    const author = LSpropsObject.authors.length === 0 ? '' : `Author: ${reverseName(LSpropsObject.authors[0].name)}`;

    const readInBrowser = LSpropsObject.formats["text/html; charset=utf-8"] || LSpropsObject.formats["text/html; charset=iso-8859-1"];

    return (
        <div className="bookpage">
            <p>{LSpropsObject.title}</p>
            <p>{author}</p>
            <p>Download count in the last 30 days: {LSpropsObject.download_count.toLocaleString()}</p>
            <p>Reading options:</p>
            <p>Read in your browser: {readInBrowser}</p>
            <p>Read via Apple Books: {LSpropsObject.formats["application/epub+zip"]}</p>

        </div>
    )
}

export default BookPage;
